/** @format */

import React from 'react';
import ReactDom from 'react-dom';
import './styles.css';
import Button from './Button';
import ComponenteA from './ComponeteA';
import ComponenteB from './ComponenteB';

function sum(a, b) {
	alert(a + b);
}

/*
function primeiroJSX() {
	//	/* prettier-ignore
	return (
    <div className='teste'>
        <h2> Introdução ao React - DIO </h2>
		<p>Soma: {sum(29, 13)}</p>
    </div>
    )
}
*/
const elemento1 = <h2>Renderização</h2>;
const paragrafo = <p>Eu sou um parágrafo</p>;

// function App() {}
const App = () => {
	// prettier-ignore
	// const soma = () =>

	return (
	<div className="App">
		<Button onClick={() => sum(19, 13)} name="Marco Fuganti" />
		<ComponenteA>
			<ComponenteB>
				<Button onClick={() => sum(42, 23)} name="Damaris" />
			</ComponenteB>
		</ComponenteA>
	</div>
	)
};

const rootElement = document.getElementById('root');
ReactDom.render(<App />, rootElement);
